func == (left: Image, right: Image) -> Bool {
    return ImagePNGRepresentation(left) == ImagePNGRepresentation(right)
}

func cleanDefaultCache() {
    clearCaches([KingfisherManager.sharedManager.cache])
}

func clearCaches(caches: [ImageCache]) {
    for c in caches {
        c.clearMemoryCache()
        c.clearDiskCache()
    }
}
